import React from 'react';
import Header from './components/header/header.component';
import Footer from './components/footer/footer.component';
import { Switch, Route } from 'react-router-dom';
import Homepage from './pages/homepage/homepage.component';
import WomensPage from './pages/womens-page/womens-page.component';
import MensPage from './pages/mens-page/mens-page.component';
import ShopPage from './pages/shop-page/shop-page.component';
import SignInAndSignUpPage from './pages/sign-in-and-sign-up-page/sign-in-and-sign-up-page.component';
import { auth } from './firebase/firebase.utils';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      currentUser: null,
    };
  }

  unsubscribeFromAuth = null;

  componentDidMount() {
    this.unsubscribeFromAuth = auth.onAuthStateChanged((user) => {
      this.setState({ currentUser: user });
    });
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }

  render() {
    return (
      <div className={`${window.innerWidth <= 768 ? 'mob' : 'desktop'}`}>
        <Header />
        <Switch>
          <Route exact path="/" component={Homepage} />
          <Route exact path="/womens" component={WomensPage} />
          <Route exact path="/mens" component={MensPage} />
          <Route exact path="/shop" component={ShopPage} />
          <Route exact path="/signIn" component={SignInAndSignUpPage} />
        </Switch>
        <Footer />
        <div className="copy-right">Copyright Â© 2021 by Castel Clothing</div>
      </div>
    );
  }
}

export default App;
