import React from 'react';
import CustomButton from '../custom-button/custom-button.component';

const SpecialOfferCard = (props) => {
  return (
    <div className="special-offers-card">
      <div
        className="image"
        style={{ backgroundImage: `url(${props.specialOffer.imageURL})` }}
      >
        <div className="ribbon">
          <div className="tag">{props.specialOffer.tag}</div>
        </div>
        <div className="quick-view">
          <i className="fas fa-search"></i>
        </div>
        <CustomButton>Add To Cart</CustomButton>
      </div>
      <div className="content">
        <h4>{props.specialOffer.title}</h4>
        <del>NRs: {props.specialOffer.mainPrice}</del>
        <h6>NRs: {props.specialOffer.price} /- </h6>
      </div>
    </div>
  );
};

export default SpecialOfferCard;
