import React from 'react';

import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper.scss';

const SpecialOfferCard = (props) => {
  const specialOffers = props.datas.special;

  const width = window.innerWidth;
  const offers = specialOffers.map((offer) => {
    //Mob
    if (width <= 760) {
      return (
        <SwiperSlide key={offer.id}>
          <div
            className="special-item-mob"
            style={{ backgroundImage: `url(${offer.img})` }}
          ></div>
        </SwiperSlide>
      );
    }
    // Desktop
    else {
      return (
        <div className="special-offers-card">
          <div
            className="image"
            style={{ backgroundImage: `url(${props.specialOffer.imageURL})` }}
          >
            <div className="ribbon">
              <div className="tag">{props.specialOffer.tag}</div>
            </div>
            <div className="quick-view">
              <i className="fas fa-search"></i>
            </div>
            <div className="add-to-cart">Add To Cart</div>
          </div>
          <div className="content">
            <h4>{props.specialOffer.title}</h4>
            <del>NRs: {props.specialOffer.mainPrice}</del>
            <h6>NRs: {props.specialOffer.price} /- </h6>
          </div>
        </div>
      );
    }
  });

  return (
    <>
      <h1 className="heading">Special Offers</h1>
      <DisplaySpecialOffer width={width} offers={offers} />
    </>
  );
};

const DisplaySpecialOffer = (props) => {
  const width = props.width;
  const offers = props.offers;

  if (width <= 760) {
    return (
      <div className="special-offer-holder">
        <Swiper
          className="special-offer-slider"
          autoHeight={true}
          spaceBetween={15}
          slidesPerView={2}
          grabCursor={true}
        >
          {offers}
        </Swiper>
      </div>
    );
  } else {
    return <div className="special-offers">{offers}</div>;
  }
};

export default SpecialOfferCard;
