import React from 'react';
import SpecialOfferCard from './special-offer-card.component';

const SpecialOfferCardList = (props) => {
  const width = window.innerWidth;
  //Mob
  if (width <= 760) {
    return (
      <div className="special-offers-card">
        <Swiper>
          <SwiperSlide>
            <div
              className="image"
              style={{ backgroundImage: `url(${props.specialOffer.imageURL})` }}
            >
              <div className="ribbon">
                <div className="tag">{props.specialOffer.tag}</div>
              </div>
              <div className="add-to-cart">Add To Cart</div>
              <div className="content">
                <h4>{props.specialOffer.title}</h4>
                <h6>NRs: {props.specialOffer.price} /- </h6>
                <div className="button">Quick View</div>
              </div>
            </div>
          </SwiperSlide>
        </Swiper>
      </div>
    );
  }
  // Desktop
  else {
    return (
      <div className="special-offers-card-list">
        {props.spcialOffers.map((specialOffer) => (
          <SpecialOfferCard key={specialOffer.id} specialOffer={specialOffer} />
        ))}
      </div>
    );
  }
};

export default SpecialOfferCardList;
