import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ReactComponent as Logo } from '../../assets/logo.svg';
import '../../styles/styles.scss';
import '../../styles/responsive.scss';

const Header = () => {
  const [width, setWidth] = useState(0);

  window.addEventListener('resize', (e) => {
    setWidth(e.currentTarget.innerWidth);
  });

  if (width) {
  }

  Content();

  return <Content />;
};

const Content = () => {
  window.addEventListener('resize', (e) => contentToLoad(e));
  const contentToLoad = (e) => {
    let width, type;
    e === undefined
      ? (width = window.innerWidth)
      : (width = e.currentTarget.innerWidth);
    width <= 760 ? (type = 'mob') : (type = 'desktop');

    if (type === 'desktop') {
      return (
        <>
          <div className="header">
            <div className="top">
              <div className="search">
                <input type="text" placeholder={'Search...'.toUpperCase()} />
                <div className="search-btn">
                  <i className="fas fa-search"></i>
                </div>
              </div>
              <div className="for-logo">
                <Logo className="logo" />
              </div>
              <div className="sign-cart">
                <div className="sign-in">
                  <i className="fas fa-user"></i> SIGN UP
                </div>
                <div className="login">
                  <i className="fas fa-user"></i> LOGIN
                </div>
                <div className="cart">
                  <i className="fas fa-shopping-cart"></i> VIEW CART
                </div>
              </div>
            </div>
          </div>
          <div className="nav-bar">
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/">What's New</Link>
              </li>
              <li>
                <Link to="/">Shop</Link>
              </li>
              <li>
                <Link to="/">Mens</Link>
              </li>
              <li>
                <Link to="/">Womens</Link>
              </li>
              <li>
                <Link to="/">Contact Us</Link>
              </li>
            </ul>
          </div>
        </>
      );
    } else {
      return <h1>hi</h1>;
    }
  };
};

// let ty = document.querySelector('.nav-bar');

// {`${(window.onload = () =>
//   (window.onscroll = () =>
//     window.scrollY > 250
//       ? ty.classList.add('sticky')
//       : ty.classList.remove('sticky')))} nav-bar`}

export default Header;
