import React from 'react';

import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper.scss';

const SpecialOfferCard = (props) => {
  const width = window.innerWidth;
  //Mob
  if (width <= 760) {
    return (
      <div className="special-offers-card">
        <Swiper>
          <SwiperSlide>
            <div
              className="image"
              style={{ backgroundImage: `url(${props.specialOffer.imageURL})` }}
            >
              <div className="ribbon">
                <div className="tag">{props.specialOffer.tag}</div>
              </div>
              <div className="add-to-cart">Add To Cart</div>
              <div className="content">
                <h4>{props.specialOffer.title}</h4>
                <h6>NRs: {props.specialOffer.price} /- </h6>
                <div className="button">Quick View</div>
              </div>
            </div>
          </SwiperSlide>
        </Swiper>
      </div>
    );
  }
  // Desktop
  else {
    return (
      <div className="special-offers-card">
        <div
          className="image"
          style={{ backgroundImage: `url(${props.specialOffer.imageURL})` }}
        >
          <div className="ribbon">
            <div className="tag">{props.specialOffer.tag}</div>
          </div>
          <div className="quick-view">
            <i className="fas fa-search"></i>
          </div>
          <div className="add-to-cart">Add To Cart</div>
        </div>
        <div className="content">
          <h4>{props.specialOffer.title}</h4>
          <del>NRs: {props.specialOffer.mainPrice}</del>
          <h6>NRs: {props.specialOffer.price} /- </h6>
        </div>
      </div>
    );
  }
};

export default SpecialOfferCard;
