import React from 'react';

const SpecialOfferCard = ({ specialOffer }) => {
  return (
    <div className="special-offers-card">
      <div
        className="image"
        style={{ backgroundImage: `url(${specialOffer.imageURL})` }}
      >
        <div className="ribbon">
          <div className="tag">{specialOffer.tag}</div>
        </div>
        <div className="quick-view">
          <i className="fas fa-search"></i>
        </div>
        <div className="add-to-cart">Add To Cart</div>
      </div>
      <div className="content">
        <h4>{specialOffer.title}</h4>
        <h6>Rs: {specialOffer.price} /-</h6>
      </div>
    </div>
  );
};

export default SpecialOfferCard;
