import React from 'react';

import arrow from '../../assets/arrow.svg';
import homeIcon from '../../assets/home.svg';
import dress from '../../assets/dress.svg';
import shop from '../../assets/shop.svg';
import newItem from '../../assets/new.svg';
import shirt from '../../assets/shirt.svg';
import contact from '../../assets/contact.svg';
import close from '../../assets/close.svg';

const MobileNav = () => {
  return (
    <>
      <div className="mobile-nav">
        <div className="cart-icon">
          <i className="fas fa-shopping-cart"></i>
        </div>
        <div className="nav-bg">
          <i className="fas fa-search"></i>
          <div className="bars" id="bars">
            <div className="bars-line"></div>
            <div className="bars-line"></div>
            <div className="bars-line"></div>
          </div>
        </div>
      </div>

      <div className="side-menu ">
        <div
          className="close-btn"
          style={{ backgroundImage: `url(${close})` }}
        ></div>
        <div className="wrapper">
          <div className="profile">
            <div
              className="image"
              style={{
                backgroundImage:
                  'url(https://images.pexels.com/photos/1036620/pexels-photo-1036620.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260)',
              }}
            ></div>
          </div>
          <h4>Please Sign In</h4>
          <div className="group">
            <div className="button">Sign Up</div>
            <div className="button">Login</div>
          </div>

          <ul className="nav-list">
            <li className="drop">
              <div className="li">
                <div className="grab">
                  <div
                    className="icon"
                    style={{ backgroundImage: `url(${homeIcon})` }}
                  />
                  Home
                </div>
                <div
                  className="arrow"
                  style={{ backgroundImage: `url(${arrow})` }}
                />
              </div>
              <ul className="nav-drop">
                <li className="">Home</li>
                <li className="">Home</li>
                <li className="">Home</li>
              </ul>
            </li>

            <li>
              <div className="li">
                <div className="grab">
                  <div
                    className="icon"
                    style={{ backgroundImage: `url(${newItem})` }}
                  />
                  What's New
                </div>
                <div
                  className="arrow"
                  style={{ backgroundImage: `url(${arrow})` }}
                />
              </div>
            </li>

            <li>
              <div className="li">
                <div className="grab">
                  <div
                    className="icon"
                    style={{ backgroundImage: `url(${shop})` }}
                  />
                  Shop
                </div>
                <div
                  className="arrow"
                  style={{ backgroundImage: `url(${arrow})` }}
                />
              </div>
            </li>

            <li>
              <div className="li">
                <div className="grab">
                  <div
                    className="icon"
                    style={{ backgroundImage: `url(${dress})` }}
                  />
                  Women's
                </div>
                <div
                  className="arrow"
                  style={{ backgroundImage: `url(${arrow})` }}
                />
              </div>
            </li>

            <li>
              <div className="li">
                <div className="grab">
                  <div
                    className="icon"
                    style={{ backgroundImage: `url(${shirt})` }}
                  />
                  Men's
                </div>
                <div
                  className="arrow"
                  style={{ backgroundImage: `url(${arrow})` }}
                />
              </div>
            </li>

            <li>
              <div className="li">
                <div className="grab">
                  <div
                    className="icon"
                    style={{ backgroundImage: `url(${contact})` }}
                  />
                  Contact Us
                </div>
                <div
                  className="arrow"
                  style={{ backgroundImage: `url(${arrow})` }}
                />
              </div>
            </li>
          </ul>
        </div>
      </div>
    </>
  );
};

window.onload = () => {
  const bars = document.querySelector('#bars');
  const sideMenu = document.querySelector('.side-menu');
  const closeBtn = document.querySelector('.close-btn');
  bars.addEventListener('click', () => {
    bars.classList.toggle('close');
    sideMenu.classList.toggle('show');
  });
  closeBtn.addEventListener('click', () => {
    bars.classList.remove('close');
    sideMenu.classList.remove('show');
  });

  const drop = document.querySelector('.drop');
  const navDrop = document.querySelector('.nav-drop');

  drop.addEventListener('click', () => {
    navDrop.classList.toggle('show-drop');
  });
};

export default MobileNav;
