import React, { useState } from 'react';

const SpecialOfferCard = (props) => {
  const [width, setWidth] = useState(0);

  window.addEventListener('resize', (e) => {
    setWidth(e.currentTarget.innerWidth);
  });

  if (width) {
  }

  Content();
  return <Content />;
};

const Content = () => {
  window.addEventListener('resize', (e) => contentToLoad(e));

  const contentToLoad = (e, props) => {
    let width, type;
    e === undefined
      ? (width = window.innerWidth)
      : (width = e.currentTarget.innerWidth);
    width <= 760 ? (type = 'mob') : (type = 'desktop');

    if (type === 'desktop') {
      return (
        <div className="special-offers-card">
          <div
            className="image"
            style={{ backgroundImage: `url(${props.specialOffer.imageURL})` }}
          >
            <div className="ribbon">
              <div className="tag">{props.specialOffer.tag}</div>
            </div>
            <div className="quick-view">
              <i className="fas fa-search"></i>
            </div>
            <div className="add-to-cart">Add To Cart</div>
          </div>
          <div className="content">
            <h4>{props.specialOffer.title}</h4>
            <h6>Rs: {props.specialOffer.price} /-</h6>
          </div>
        </div>
      );
    } else {
      return null;
    }
  };

  return contentToLoad();
};

export default SpecialOfferCard;
