import React from 'react';
import '../../styles/styles.scss';
// import Swiper core and required modules
import { Swiper, SwiperSlide } from 'swiper/react';
// Import Swiper styles
import 'swiper/swiper.scss';
import 'swiper/components/effect-cube/effect-cube.min.css';
import 'swiper/components/pagination/pagination.min.css';
// import Swiper core and required modules
import SwiperCore, { EffectCube, Autoplay } from 'swiper/core';
// install Swiper modules
SwiperCore.use([EffectCube, Autoplay]);

const Slider = ({ slider }) => {
  return (
    <Swiper
      className="mySwiper"
      effect={'cube'}
      grabCursor={true}
      cubeEffect={{
        shadow: true,
        slideShadows: true,
        shadowOffset: 1,
      }}
      spaceBetween={0}
      slidesPerView={1}
      loop={true}
      autoplay={{
        delay: 2500,
        disableOnInteraction: false,
      }}
    >
      {slider.map((slide) => (
        <SwiperSlide key={slide.id}>
          <div className="slider">
            <div className="container">
              <div className="wrapper">
                <div className="content">
                  <h6>Type: {slide.type}</h6>
                  <h3>{slide.title.toUpperCase()}</h3>
                  <h4>Price: Rs {slide.price}/-</h4>
                  <div to="/" className="custom-button">
                    View
                  </div>
                  <div to="/" className="custom-btn">
                    Add To Cart
                  </div>
                </div>
              </div>
              <div className="wrappers ">
                <div className="image-bg">
                  <div
                    className="slider-image"
                    style={{ backgroundImage: `url(${slide.imageURL})` }}
                  ></div>
                </div>
              </div>
            </div>
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
};

export default Slider;
